import { FlattenInterpolation, ThemeProps } from 'styled-components';

declare module '@devseed-ui/animation' {
  /**
   * Keyframe animation: shake.
   */
  function shake(): FlattenInterpolation<ThemeProps<any>>;

  /**
   * Keyframe animation: rotate clockwise.
   */
  function rotateCW(): FlattenInterpolation<ThemeProps<any>>;

  /**
   * Keyframe animation: rotate counter clockwise.
   */
  function rotateCCW(): FlattenInterpolation<ThemeProps<any>>;

  /**
   * Keyframe animation: reveal (fade-in).
   */
  function reveal(): FlattenInterpolation<ThemeProps<any>>;

  /**
   * Keyframe animation: hide (fade-out).
   */
  function hide(): FlattenInterpolation<ThemeProps<any>>;
}
