import {
  DefaultTheme,
  FlattenInterpolation,
  StyledComponent,
  ThemeProps
} from 'styled-components';

declare module '@devseed-ui/button' {
  interface ButtonGroupProps {
    children?: React.ReactNode;

    /**
     * Button variation to render.
     * A regular variation is a combination of <color-name>-<text|fill|outline>
     * Achromic versions are only outline or text.
     *
     * @default base-text
     */
    variation?: ButtonProps['variation'];

    /**
     * The button size.
     * @default medium
     */
    size?: ButtonProps['size'];

    /**
     * The value for the corner radius.
     * @default rounded
     */
    radius?: ButtonProps['radius'];
  }

  /**
   * Renders a Button Group element.
   * Buttons inside the button group will react to the supported props passed to
   * the group: variation, size, radius
   */
  export const ButtonGroup: StyledComponent<
    'div',
    DefaultTheme,
    ButtonGroupProps,
    never
  >;

  /**
   * Renders the css needed to create a button.
   *
   * @param params Same props as passed to a button component
   */
  function createButtonGroupStyles(
    params: ButtonProps
  ): FlattenInterpolation<ThemeProps<ButtonGroupProps>>;
}
