import './ButtonGroup';

declare module '@devseed-ui/button' {
  /**
   * Context provider for the button group.
   * Note: Only use if you know what you're doing. This is not normally needed.
   */
  function ButtonGroupProvider(props: ButtonGroupProps): JSX.Element;

  /**
   * Allows the use of a button group context inside another element.
   * This is useful to create elements that respond to a button group.
   */
  function useButtonGroup(): Pick<
    ButtonGroupProps,
    'variation' | 'size' | 'radius'
  > & {
    /**
     * Whether or not this element is currently inside a Button
     */
    inGroup: boolean;
  };
}
