import React from 'react';
import styled from 'styled-components';
import { cleanup } from '@testing-library/react';

import { renderWithTheme } from '../../../../test/_setup-theme-provider';
import createFormSkinStyles from '.';

const FormSkinStylesTest = styled.textarea`
  ${createFormSkinStyles}
`;

afterAll(cleanup);

describe('<Form controlSkin />', () => {
  it('renders createFormSkinStylesTest', () => {
    const rendered = renderWithTheme(<FormSkinStylesTest />);
    expect(rendered.asFragment()).toMatchSnapshot();
  });

  it('renders createFormSkinStylesTest with props', () => {
    const rendered = renderWithTheme(<FormSkinStylesTest size='large' />);
    expect(rendered.asFragment()).toMatchSnapshot();
  });
});
