import React from 'react';
import { cleanup } from '@testing-library/react';

import { renderWithTheme } from '../../../../test/_setup-theme-provider';
import {
  FormFieldset,
  FormFieldsetHeader,
  FormLegend,
  FormFieldsetBody
} from '.';

afterAll(cleanup);

describe('<FormFieldset />', () => {
  it('renders <FormFieldset />', () => {
    expect(
      renderWithTheme(<FormFieldset>content</FormFieldset>).asFragment()
    ).toMatchSnapshot();
  });

  it('renders <FormFieldsetHeader />', () => {
    expect(
      renderWithTheme(
        <FormFieldsetHeader>content</FormFieldsetHeader>
      ).asFragment()
    ).toMatchSnapshot();
  });

  it('renders <FormLegend />', () => {
    expect(
      renderWithTheme(<FormLegend>content</FormLegend>).asFragment()
    ).toMatchSnapshot();
  });

  it('renders <FormFieldsetBody />', () => {
    expect(
      renderWithTheme(<FormFieldsetBody>content</FormFieldsetBody>).asFragment()
    ).toMatchSnapshot();
  });
});
