import { ButtonProps } from "@devseed-ui/button";

declare module '@devseed-ui/toolbar' {
  /**
   * Context provider for the toolbar.
   * Note: Only use if you know what you're doing. This is not normally needed.
   */
  function ToolbarProvider(props: ToolbarProps): JSX.Element;

  /**
   * Allows the use of a toolbar context inside another element.
   * This is useful to create elements that respond to a toolbar.
   */
  function useToolbar(): ToolbarProps & {
    /**
     * Whether or not this element is currently inside a Toolbar
     */
    inToolbar: boolean,

    size: ButtonProps['size'];
  };
}
