import {
  FlattenInterpolation,
  ThemeProps,
  DefaultTheme,
  StyledComponent
} from 'styled-components';

declare module '@devseed-ui/typography' {
  /**
   * Renders a Prose element which applies styles to html elements regardless of
   * them being styled components.
   * This is useful to wrap content over which the developer has no render
   * control (e.g. Markdown renderer)
   */
  export const Prose: StyledComponent<'div', DefaultTheme, {}, never>;

  /**
   * Renders the css needed to create a Prose element.
   */
  function createProseStyles(): FlattenInterpolation<ThemeProps<any>>;
}
