import {
  FlattenInterpolation,
  ThemeProps,
  DefaultTheme,
  StyledComponent
} from 'styled-components';

declare module '@devseed-ui/typography' {
  interface TableProps {
    /**
     * Whether or not to render a striped table.
     */
    zebra?: boolean;
  }

  /**
   * Renders a Table element.
   */
  export const Table: StyledComponent<'div', DefaultTheme, TableProps, never>;

  /**
   * Renders the css needed to create a Table.
   *
   * @param params Same props as passed to a Table component
   */
  function createTableStyles(
    props: TableProps
  ): FlattenInterpolation<ThemeProps<TableProps>>;
}
